package com.example.a5equiz.adaptersimport android.content.Contextimport android.content.Intentimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.TextViewimport androidx.recyclerview.widget.RecyclerViewimport com.example.a5equiz.Rimport com.example.a5equiz.activities.DetailsActivityimport com.example.a5equiz.models.Customerimport java.text.SimpleDateFormatimport java.util.Dateimport java.util.Localeclass CustomerAdapter(private val context: Context, private val customers: List<Customer>) :    RecyclerView.Adapter<CustomerAdapter.CustomerViewHolder>() {    inner class CustomerViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {        val customerName: TextView = itemView.findViewById(R.id.customerName)        val phoneCustomer: TextView = itemView.findViewById(R.id.phoneCustomer)        val phoneModel: TextView = itemView.findViewById(R.id.phoneModel)        val issue: TextView = itemView.findViewById(R.id.issue)        val status: TextView = itemView.findViewById(R.id.status)        val montantNormal: TextView = itemView.findViewById(R.id.montantNormal)        val montantNegocie: TextView = itemView.findViewById(R.id.montantNegocie)        val repairDate: TextView = itemView.findViewById(R.id.repairDate)    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): CustomerViewHolder {        val view =            LayoutInflater.from(parent.context).inflate(R.layout.item_customer, parent, false)        return CustomerViewHolder(view)    }    override fun onBindViewHolder(holder: CustomerViewHolder, position: Int) {        val customer = customers[position]        holder.customerName.text = customer.customerName        holder.phoneCustomer.text = customer.phoneCustomer        holder.phoneModel.text = customer.phoneModel        holder.issue.text = customer.issue        holder.status.text = customer.status        holder.montantNormal.text = customer.montantNormal        holder.montantNegocie.text = customer.montantNegocie        holder.repairDate.text = SimpleDateFormat("dd/MM/yyyy HH:mm", Locale.getDefault()).format(            Date(customer.repairDate)        )        holder.itemView.setOnClickListener {            val intent = Intent(context, DetailsActivity::class.java).apply {                putExtra("customerName", customer.customerName)                putExtra("description", customer.description)                putExtra("phoneCustomer", customer.phoneCustomer)                putExtra("phoneModel", customer.phoneModel)                putExtra("issue", customer.issue)                putExtra("status", customer.status)                putExtra("montantNormal", customer.montantNormal)                putExtra("montantNegocie", customer.montantNegocie)                putExtra("repairDate", customer.repairDate)            }            context.startActivity(intent)        }    }    override fun getItemCount() = customers.size}