package com.example.a5equiz.adaptersimport android.content.Contextimport android.content.Intentimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.TextViewimport androidx.recyclerview.widget.RecyclerViewimport com.example.a5equiz.Rimport com.example.a5equiz.activities.DetailsActivityimport com.example.a5equiz.models.Customerclass EnregistreAdapter(private val context: Context, private var dataSource: MutableList<Customer>) :    RecyclerView.Adapter<EnregistreAdapter.EnregistreHolder>() {    inner class EnregistreHolder(view: View) : RecyclerView.ViewHolder(view) {        val phoneCustomer: TextView = view.findViewById(R.id.phoneCustomer)        val description: TextView = view.findViewById(R.id.description)        val customerName: TextView = view.findViewById(R.id.customerName)        val phoneModel: TextView = view.findViewById(R.id.phoneModel)        val issue: TextView = view.findViewById(R.id.issue)        val status: TextView = view.findViewById(R.id.status)        val montantNormal: TextView = view.findViewById(R.id.montantNormal)        val montantNegocie: TextView = view.findViewById(R.id.montantNegocie)        val repairDate: TextView = view.findViewById(R.id.repairDate)    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): EnregistreHolder {        val view = LayoutInflater.from(context).inflate(R.layout.enregistre_holder, parent, false)        return EnregistreHolder(view)    }    override fun getItemCount(): Int = dataSource.size    override fun onBindViewHolder(holder: EnregistreHolder, position: Int) {        val customer = dataSource[position]        holder.customerName.text = customer.customerName        holder.description.text = customer.description        holder.phoneCustomer.text = customer.phoneCustomer        holder.phoneModel.text = customer.phoneModel        holder.issue.text = customer.issue        holder.status.text = customer.status        holder.montantNegocie.text = customer.montantNegocie        holder.montantNormal.text = customer.montantNormal        holder.repairDate.text = customer.repairDate        holder.itemView.setOnClickListener {            val intent = Intent(context, DetailsActivity::class.java).apply {                putExtra("customerName", customer.customerName)                putExtra("description", customer.description)                putExtra("phoneCustomer", customer.phoneCustomer)                putExtra("phoneModel", customer.phoneModel)                putExtra("issue", customer.issue)                putExtra("status", customer.status)                putExtra("montantNormal", customer.montantNormal)                putExtra("montantNegocie", customer.montantNegocie)                putExtra("repairDate", customer.repairDate)            }            context.startActivity(intent)        }    }}