package com.example.a5equiz.basesimport android.widget.ImageViewimport android.widget.LinearLayoutimport android.widget.TextViewimport android.widget.Toastimport androidx.fragment.app.Fragmentimport com.example.a5equiz.Rimport com.example.a5equiz.config.ConstToastopen class BaseFragment: Fragment() {    fun showToast(toastType: Int, message: CharSequence) {        val toastView = layoutInflater.inflate(            R.layout.layout_toast,            view?.findViewById(R.id.layoutToastContainer)        )        val toastLayoutContainer = toastView.findViewById<LinearLayout>(R.id.layoutToastContainer)        val imageIcon = toastView.findViewById<ImageView>(R.id.imageIcon)        val textMessage = toastView.findViewById<TextView>(R.id.textMessage)        when (toastType) {            ConstToast.TOAST_TYPE_SUCCESS -> {                toastLayoutContainer.setBackgroundResource(R.drawable.bg_toast_success)                imageIcon.setImageResource(R.drawable.ic_check_circle)                textMessage.text = message            }            ConstToast.TOAST_TYPE_ERROR -> {                toastLayoutContainer.setBackgroundResource(R.drawable.bg_toast_error)                imageIcon.setImageResource(R.drawable.ic_error)                textMessage.text = message            }            ConstToast.TOAST_TYPE_WARNING -> {                toastLayoutContainer.setBackgroundResource(R.drawable.bg_toast_warning)                imageIcon.setImageResource(R.drawable.ic_warning)                textMessage.text = message            }        }        with(Toast(activity)) {            duration = Toast.LENGTH_SHORT            view = toastView            show()        }    }}